jQuery(document).ready(function($) {
    var _loading_button = Ladda.create( document.querySelector( '.ladda-button' ) );

    var MailMan = MailmanLocalized;
    // console.log(MailMan);

    $('#'+MailMan.ID).validate({
        errorPlacement: function(error, element) {
            error.insertAfter(element);
        },
        rules: {
            // "contact[name]": 'required',
            "contact[email]": {
                required: true,
                email: true,
            },
            "contact[email]": 'required',
            "contact[message]": {
                required: true,
                minlength: 2,
            },
        },
        messages: {
            "contact[name]": 'Name is required',
            "contact[email]": {
                required: "The email is required",
                email: "Please enter a valid email address.",
            },
            "contact[message]": {
                required: "Please enter your message",
                minlength: "Please write atleast a sentence."
            },
        }
    });

    // MailMan.ID = "-xx";
    $('#'+MailMan.ID).find('input').change(function (e) {
        $('#'+MailMan.ID).find('[type="submit"]').text('Send Request');
    });

    $('#'+MailMan.ID).attr('action', 'send_message');

    $('#'+MailMan.ID).submit(function(e){
        e.preventDefault();

        var _url = MailMan.url,
            _Form = $(this),
            _button = _Form.find('[type="submit"]'),
            _mailman_message = MailMan.options.message, t;

        if( $('#'+MailMan.ID).valid() )
        {
            $.ajax({
                type: "POST",
                url: MailMan.ajaxurl,
                data: _Form.serialize(),
                dataType: 'json',
                // encode: true,

                beforeSend: function(){
                    _button.text('Sending...');
                    _loading_button.start();
                    (function progressLoop(i)
                    {
                        var t = setTimeout(function () {
                            // console.log(i/100);
                            _loading_button.setProgress(i/100);
                            if (++i <= 100) progressLoop(i);      //  increment i and call myLoop again if i <= 100
                            if (i==100) { _loading_button.stop(); clearTimeout(t); }
                        }, 90)
                    })(0);
                },

                success: function(data){
                    // var data = $.parseJSON(data);
                    console.log("SUCCESS DATA", data);
                    // console.log(data.method);
                    if( data.message == 1 ) {
                        mailman_prompt(
                            'success',
                            _mailman_message.success.body,
                            _mailman_message.success.title,
                            function(_alert){
                                $(document).on('click', '#mail-man-alert .close', function(){
                                    $(_Form)[0].reset();
                                    _button.text('Send Request');
                                });
                                $(document).on('click', '.message-prompt-fixed-container', function(){
                                    $(_alert).find('.close').click();
                                });
                            }
                        );
                    } else {
                        var errorsDiv = $('<div class="alert alert-warning"><button type="button" class="close" data-dismiss="alert" aria-hidden="true">&times;</button></div>');
                        var errorsList = "<p><strong>There were errors while sending the email:</strong></p><ul style='word-wrap: break-word;'>";
                        for (var i in data.errors) {
                            errorsList = errorsList + '<li>'+data.errors[i]+'</li>';
                        };
                        errorsList = errorsList + "</ul>";
                        errorsDiv.append( errorsList );
                        $(_Form).find('.alert').remove();
                        $(_Form).prepend( errorsDiv.fadeIn(800) );
                    }
                    _loading_button.remove();
                    _button.text('Request Sent!');
                },
            })
            .fail(function (data) {
                var data = $.parseJSON(data);
                console.log("FAILED", data);
                mailman_prompt(
                    'error',
                    _mailman_message.fail.body,
                    data.responseText,
                    function(_alert){
                        $(document).on('click', '#mail-man-alert .close', function(){
                            $(_Form)[0].reset();
                        });
                        $(document).on('click', '.message-prompt-fixed-container', function(){
                            $(_alert).find('.close').click();
                        });
                    }
                );
                _button.text('Send');
            })
            .done(function (data){
                console.log("DONE", $.parseJSON(data));
                _button.text('Sent!');
                _loading_button.remove();
            });
        }
        else
        {
            // Not avalid
            _loading_button.stop();
        }
    });

});

/*
| ---------------------------------
| # Prompt
| ---------------------------------
| JS Equivalent of MailMailer->prompt();
*/
function mailman_prompt($type, $message, $title, $callback)
{
    if (null == $callback) {
        $callback = function(){};
    }
    var id = "mail-man-alert";
    // var _alert = jQuery('<div class="alert message-prompt-fixed-container"><div id="'+id+'" class="alert-gold" data-alert-type="'+($type)+'"><button type="button" class="close" data-dismiss="alert" aria-hidden="true">&times;</button><div class="text-center"><h3 class="text-uppercase">'+($title)+'</h3><p>'+nl2br($message)+'</p></div></div></div>');
    var _alert = jQuery('<div class="alert message-prompt-fixed-container"><div id="'+id+'" class="alert-gold" data-alert-type="'+($type)+'"><button type="button" class="close" data-dismiss="alert" aria-hidden="true">&times;</button><div class="container-fluid"><div class="col-md-5"><div class="beat-wave one"></div><div class="beat-wave two"></div><div class="beat-wave three"></div></div><div class="col-md-7"><div class="text-left text-md-left"><h3 class="text-uppercase">'+($title)+'</h3><p>'+nl2br($message)+'</p></div></div></div></div></div>');
    jQuery('body').append(_alert);
    $callback('#'+id);
    return false;
}

function nl2br (str, is_xhtml) {
    var breakTag = (is_xhtml || typeof is_xhtml === 'undefined') ? '<br />' : '<br>';
    return (str + '').replace(/([^>\r\n]?)(\r\n|\n\r|\r|\n)/g, '$1' + breakTag + '$2');
}

jQuery(function ($) {
    $('.list-group.checked-list-box .list-group-item').each(function () {

        // Settings
        var $widget = $(this),
            $checkbox = $('<input type="checkbox" class="hidden" />'),
            color = ($widget.data('color') ? $widget.data('color') : "primary"),
            style = ($widget.data('style') == "button" ? "btn-" : "list-group-item-"),
            settings = {
                on: {
                    icon: 'glyphicon glyphicon-check'
                },
                off: {
                    icon: 'glyphicon glyphicon-unchecked'
                }
            };

        $widget.css('cursor', 'pointer')
        $widget.append($checkbox);

        // Event Handlers
        $widget.on('click', function () {
            $checkbox.prop('checked', !$checkbox.is(':checked'));
            $checkbox.triggerHandler('change');
            updateDisplay();
        });
        $checkbox.on('change', function () {
            updateDisplay();
        });


        // Actions
        function updateDisplay() {
            var isChecked = $checkbox.is(':checked');

            // Set the button's state
            $widget.data('state', (isChecked) ? "on" : "off");

            // Set the button's icon
            $widget.find('.state-icon')
                .removeClass()
                .addClass('state-icon ' + settings[$widget.data('state')].icon);

            // Update the button's color
            if (isChecked) {
                $widget.addClass(style + color + ' active');
            } else {
                $widget.removeClass(style + color + ' active');
            }
        }

        // Initialization
        function init() {

            if ($widget.data('checked') == true) {
                $checkbox.prop('checked', !$checkbox.is(':checked'));
            }

            updateDisplay();

            // Inject the icon if applicable
            if ($widget.find('.state-icon').length == 0) {
                $widget.prepend('<span class="state-icon ' + settings[$widget.data('state')].icon + '"></span>');
            }
        }
        init();
    });

    $('#get-checked-data').on('click', function(event) {
        event.preventDefault();
        var checkedItems = {}, counter = 0;
        $("#check-list-box li.active").each(function(idx, li) {
            checkedItems[counter] = $(li).text();
            counter++;
        });
        $('#display-json').html(JSON.stringify(checkedItems, null, '\t'));
    });
});